import api from "./index";

const getReviews = (id) => api.get("/review/get/" + id);
const getName = (c_id) => api.get("/userDetails/" + c_id);
const postReview = (data) => api.post("/review/post/", data);

export default {
  getReviews,
  getName,
  postReview,
};
