import React from "react";
import { StyleSheet, Text, View } from "react-native";
import colors from "../config/colors";
import { FontAwesome } from "@expo/vector-icons";
import { FontAwesome5 } from "@expo/vector-icons";
import { MaterialCommunityIcons } from "@expo/vector-icons";

function RequestItem({ provider_name, provider_rate, date_time, status }) {
  var box = [];
  if (status == "Accepted") {
    box.push(
      <View style={styles.accepted}>
        <Text style={styles.box_text}>Accepted</Text>
        <FontAwesome name="check-circle" size={20} color="white" />
      </View>
    );
  }
  if (status == "Rejected") {
    box.push(
      <View style={styles.rejected}>
        <Text style={styles.box_text}>Rejected</Text>
        <FontAwesome5 name="exclamation-circle" size={20} color="white" />
      </View>
    );
  }
  if (status == "Pending") {
    box.push(
      <View style={styles.pending}>
        <Text style={styles.box_text}>Pending</Text>
        <MaterialCommunityIcons
          name="dots-horizontal-circle"
          size={20}
          color="white"
        />
      </View>
    );
  }
  return (
    <View style={styles.container}>
      <View style={styles.line}>
        <Text style={styles.provider_name}>{provider_name}</Text>
      </View>
      <Text style={styles.provider_rate}>Rate per hour: {provider_rate}LBP</Text>
      <Text>{date_time}</Text>
      <View style={styles.box}>{box}</View>
    </View>
  );
}

const styles = StyleSheet.create({
  accepted: {
    backgroundColor: "green",
    flexDirection: "row",
    justifyContent: "space-between",
    padding: 10,
    borderRadius: 15,
  },
  box: {
    alignSelf: "flex-end",
    margin: 5,
    maxWidth: 110,
    width: 110,
  },
  box_text: {
    color: colors.white,
    fontWeight: "bold",
    marginHorizontal: 3,
  },
  container: {
    backgroundColor: colors.babygrey,
    flex: 1,
    padding: 10,
    marginBottom: 12,
    borderRadius: 15,
  },
  line: {
    paddingTop: 5,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  pending: {
    backgroundColor: "grey",
    flexDirection: "row",
    justifyContent: "space-between",
    padding: 10,
    borderRadius: 15,
  },
  provider_rate: {
    color: colors.black,
    marginTop: 6,
    fontSize: 14,
  },
  provider_name: {
    fontWeight: "bold",
  },
  rejected: {
    backgroundColor: "red",
    flexDirection: "row",
    justifyContent: "space-between",
    padding: 10,
    borderRadius: 15,
  },
});

export default RequestItem;
