import React from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  TouchableOpacity,
  ImageBackground,
  Image,
} from "react-native";
import Svg from "react-native-svg";
import { Path as SvgPath } from "react-native-svg";
import colors from "../../config/colors";
import svg from "../../config/svg";
import { Octicons } from "@expo/vector-icons";
import { FontAwesome5 } from '@expo/vector-icons'; 

const image = require("../../assets/icon.jpg");
export default function ClientHome({ navigation }) {
  const handleFixingPress = () => {
    navigation.navigate("Browse", { category: "Fixing" });
  };

  const handleCleaningPress = () => {
    navigation.navigate("Browse", { category: "Cleaning" });
  };

  const handleMovingPress = () => {
    navigation.navigate("Browse", { category: "Moving" });
  };

  const handleOtherPress = () => {
    navigation.navigate("Browse", { category: "Other" });
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.text_container}>
        <Image source={image} style={styles.icon} />
        <Text style={styles.text}>Choose a category</Text>
      </View>
      <View style={styles.row}>
        <TouchableOpacity onPress={handleFixingPress}>
          <View style={styles.rectangle}>
              <Octicons name="tools" size={50} color="white" />
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={handleCleaningPress}>
          <View style={styles.rectangle}>
          <FontAwesome5 name="soap" size={24} color="white" />
          </View>
        </TouchableOpacity>
      </View>
      <View style={styles.row}>
        <TouchableOpacity onPress={handleMovingPress}>
          <View style={styles.rectangle}>
            <View style={styles.box}>
              <Svg
                fill={colors.white}
                preserveAspectRatio="none"
                viewBox="0 16 72 63"
              >
                <SvgPath d={svg.moving_svg} />
              </Svg>
            </View>
          </View>
        </TouchableOpacity>
        <TouchableOpacity onPress={handleOtherPress}>
          <View style={styles.rectangle}>
            <View style={styles.box}>
              <Svg
                fill={colors.white}
                preserveAspectRatio="none"
                viewBox="0 0 512 512"
              >
                <SvgPath d={svg.other_svg} />
              </Svg>
            </View>
          </View>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
  },
  box: {
    opacity: 1,
    width: "80%",
    height: "80%",
  },
  container: {
    flex: 1,
    marginTop: 30,
    justifyContent: "center",
    alignItems: "center",
    padding: 30,
  },
  icon: {
    width: 100,
    height: 100,
  },
  rectangle: {
    opacity: 1,
    backgroundColor: colors.lightblue,
    borderRadius: 19,
    width: 120,
    height: 120,
    padding: 20,
    justifyContent: "center",
    alignItems: "center",
    marginHorizontal: 10,
  },
  row: {
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "row",
    padding: 10,
  },
  svg: {
    justifyContent: "center",
    alignItems: "center",
  },
  text: {
    fontSize: 25,
    fontWeight: "600",
    color: colors.black,
    justifyContent: "center",
    alignItems: "center",
    padding: 15,
  },
  text_container: {
    justifyContent: "center",
    alignItems: "center",
  },
  textcategory: {
    color: colors.white,
    fontWeight: "bold",
  },
});
