import React from "react";
import { StyleSheet, Text, View } from "react-native";
import colors from "../config/colors";

function RequestItem({ provider_name, provider_rate, date_time, status }) {
    var box = [];
  if (status == "Accepted") {
    box.push(
      <View style={styles.accepted}>
        <Text sty>Accepted</Text>
      </View>
    );
  }
  return (
    <View style={styles.container}>
      <View style={styles.line}>
        <Text numberOfLines={1} style={styles.provider_name}>
          {provider_name}
        </Text>
      </View>
      <Text style={styles.provider_rate}>{provider_rate}</Text>
      <Text>{date_time}</Text>
      <View> {box} </View>

    </View>
  );
}

const styles = StyleSheet.create({
    accepted:{
        backgroundColor: "green"
    },
    box_text:{
        color: colors.white,
        fontWeight: "bold",
    },
  container: {
    backgroundColor: colors.babygrey,
    width: "100%",
    flex: 1,
    padding: 10,
    marginBottom: 5,
  },
  line: {
    paddingTop: 5,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  provider_rate: {
    color: colors.black,
    marginTop: 6,
    fontStyle: "italic",
  },
  provider_name: {
    fontWeight: "bold",
  },
});

export default RequestItem;
