import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, TouchableOpacity, Image } from "react-native";
import { useRoute } from "@react-navigation/native";
import colors from "../config/colors";
import { Path as SvgPath } from "react-native-svg";
import Svg from "react-native-svg";
import { MaterialIcons } from "@expo/vector-icons";
import svg from "../config/svg";

function ProviderDetails() {
  const route = useRoute();

  return (
    <View style={styles.detailscontainer}>
      <Image style={styles.image} source={route.params.image} />
      <View style={styles.textcontainer}>
        <View style={styles.ratingcontainer}>
          <Text style={styles.name}>{route.params.name} </Text>
          <Text style={styles.rating}>{route.params.rating}</Text>
          <Svg
            style={styles.star}
            fill={colors.myyellow}
            strokeWidth="30"
            stroke={colors.black}
            preserveAspectRatio="none"
            viewBox="0 0 612 792"
          >
            <SvgPath d={svg.star_svg} />
          </Svg>
        </View>
        <Text style={styles.location}>{route.params.location}</Text>
        <Text style={styles.bio}>This is my Bio</Text>
        <TouchableOpacity
          style={styles.button}
          onPress={() => console.log("Go To Chat Screen")}
        >
          <MaterialIcons name="email" size={30} color={colors.myblue} />
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  bio: {
    fontSize: 14,
  },
  detailscontainer: {
    maxHeight: 300,
    justifyContent: "flex-start",
    flexDirection: "row",
    padding: 10,
    alignContent: "flex-start",
  },
  image: {
    height: 100,
    width: 100,
    borderRadius: 50,
    borderWidth: 5,
    borderColor: colors.myblue,
  },
  location: {
    fontSize: 16,
  },
  name: {
    fontSize: 18,
    fontWeight: "bold",
  },
  textcontainer: {
    alignContent: "center",
    justifyContent: "center",
  },
  rating: {
    fontSize: 20,
    fontWeight: "bold",
  },
  ratingcontainer: {
    flex: 1,
    flexDirection: "row",
  },
  star: {
    marginLeft: 8,
    width: 20,
    height: 25,
  },
});
export default ProviderDetails;
