    //   }
    //   if (!password) {
    //     setErrortext("Enter Valid Email and Password");
    //   }

    //   let data = {
    //     email: email,
    //     password: password,
    //     remember_token: remember,
    //   };
    //   axios
    //     .post("http://127.0.0.1:8000/api/login", data)
    //     .then((response) => {
    //       console.log(response.data.is_provider);
    //       if (response.status === 200) {
    //         navigation.replace("UserTypeNav", {
    //           usertype: response.data.is_provider,
    //         });
    //         CookieService.set(
    //           "access_token",
    //           response.data.access
    //         );
    //       } else {
    //         alert("invalid username and password");
    //       }
    //     })
    //     .catch((error) => {
    //       let errortext = error;
    //       console.log(error);
    //     });

    //   handleRemember;
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Login</Text>
      <TextInput
        style={styles.inputBox}
        onChangeText={(email) => setEmail(email)}
        placeholder="Enter Email"
        placeholderTextColor={colors.text_holder}
        autoCapitalize="none"
        keyboardType="email-address"
        returnKeyType="next"
      />
      <TextInput
        style={styles.inputBox}
        onChangeText={(password) => setPassword(password)}
        placeholder="Enter Password"
        placeholderTextColor={colors.text_holder}
        keyboardType="default"
        ref={passwordInputRef}
        secureTextEntry
        returnKeyType="next"
      />
      <ErrorMessage error={errortext} />
      <TouchableOpacity style={styles.button} onPress={handleSubmitPress}>
        <Text style={styles.buttonText}>Submit</Text>
      </TouchableOpacity>
      <Text style={{ fontSize: 16, color: colors.black, marginBottom: 10 }}>
        Don't have an account?
      </Text>
      <TouchableOpacity
        style={styles.link}
        onPress={() => navigation.navigate("SignupScreen")}
      >
        <Text style={styles.link}>Signup</Text>
      </TouchableOpacity>
    </View>
  );
};
export default LoginScreen;

const styles = StyleSheet.create({
  button: {
    backgroundColor: colors.myblue,
    width: 100,
    marginVertical: 10,
    height: 50,
    marginBottom: 30,
    marginTop: 30,
    justifyContent: "center",
  },
  buttonText: {
    fontSize: 16,
    fontWeight: "bold",
    color: colors.white,
    textAlign: "center",
  },
  container: {
    backgroundColor: colors.white,
    padding: 40,
