import React, { useState } from "react";
import { Rating } from "react-native-ratings";
import {
  Alert,
  Modal,
  StyleSheet,
  Text,
  TouchableHighlight,
  View,
  TextInput,
  TouchableOpacity,
} from "react-native";
import colors from "../config/colors";

export default function AddReview() {
  const [modalVisible, setModalVisible] = useState(false);
  const [text, setText] = useState("");
  const [rating, setRating] = useState();

  const ratingCompleted = (rating) => {
    setRating(rating);
  };

  return (
    <View style={styles.centeredView}>
      {/* <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
        }}
      > */}
      <View style={styles.centeredView}>
        <View style={styles.modalView}>
          <Text style={styles.modalText}>Leave a Review</Text>
          <Rating
            type="custom"
            showRating
            ratingTextColor={none}
            onFinishRating={ratingCompleted}
            imageSize={30}
            style={{ paddingVertical: 3 }}
          />

          <TextInput
            style={styles.inputBox}
            multiline={true}
            onChangeText={(text) => setText(text)}
            placeholder="Explain your experience"
            placeholderTextColor={colors.text_holder}
          />

          <TouchableOpacity style={styles.button} onPress={console.log("done")}>
            <Text style={styles.buttonText}>Done</Text>
          </TouchableOpacity>

          {/* <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
              onPress={() => {
                setModalVisible(!modalVisible);
              }}
            >
              <Text style={styles.textStyle}>Hide Modal</Text>
            </TouchableHighlight> */}
        </View>
      </View>
      {/* </Modal> */}

      {/* <TouchableHighlight
        style={styles.openButton}
        onPress={() => {
          setModalVisible(true);
        }}
      >
        <Text style={styles.textStyle}>Show Modal</Text>
      </TouchableHighlight> */}
    </View>
  );
}

const styles = StyleSheet.create({
  button: {
    backgroundColor: colors.myblue,
    width: 80,
    height: 40,
    justifyContent: "center",
  },
  buttonText: {
    fontSize: 15,
    fontWeight: "bold",
    color: colors.white,
    textAlign: "center",
  },
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: colors.dark_blue,
    shadowOffset: {
      width: 2,
      height: 4,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  openButton: {
    backgroundColor: "#F194FF",
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  inputBox: {
    backgroundColor: colors.white,
    borderWidth: 1,
    borderColor: colors.black,
    paddingHorizontal: 16,
    fontSize: 16,
    color: colors.white,
    marginVertical: 10,
    marginBottom: 20,
    height: 300,
    color: colors.black,
    flexDirection: "column",
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 20,
    color: colors.myblue,
  },
});
