import React, { useState } from "react";
import { StyleSheet, View, TouchableHighlight, ScrollView } from "react-native";
import { FlatList } from "react-native";
import ListItem from "../components/ListItem";
import ListItemDeleteAction from "../components/ListItemDeleteAction";
import Constants from "expo-constants";
import { useNavigation, useRoute } from "@react-navigation/native";


const  initialMessages= [
  {
    id: '1',
    title: 'Jenny Doe',
    image: require('../assets/users/user-3.jpg'),
    messageTime: '4 mins ago',
    subTitle:
      'Hey there, this is my test for a post of my social app in React Native.',
  },
  {
    id: '2',
    name: 'John Doe',
    image: require('../assets/users/user-1.jpg'),
    time: '2 hours ago',
    subTitle:
      'Hey there, this is my test for a post of my social app in React Native.',
  },
  {
    id: '3',
    name: 'Ken William',
    image: require('../assets/users/user-4.jpg'),
    time: '1 hours ago',
    subTitle:
      'Hey there, this is my test for a post of my social app in React Native.',
  },
  {
    id: '4',
    userName: 'Selina Paul',
    userImg: require('../assets/users/user-6.jpg'),
    messageTime: '1 day ago',
    messageText:
      'Hey there, this is my test for a post of my social app in React Native.',
  },
  {
    id: '5',
    userName: 'Christy Alex',
    userImg: require('../assets/users/user-7.jpg'),
    messageTime: '2 days ago',
    messageText:
      'Hey there, this is my test for a post of my social app in React Native.',
  },
];

function MessagesScreen() {
  const navigation = useNavigation();
  const [messages, setMessages] = useState(initialMessages);
  const [refreshing, setRefreshing] = useState(false);

  const handleDelete = (message) => {
    setMessages(messages.filter((m) => m.id !== message.id));
  };

  return (
    <View style={styles.layout}>
      <FlatList
        data={messages}
        keyExtractor={(message) => message.id.toString()}
        renderItem={({ item }) => (
          <ListItem
            title={item.title}
            subTitle={item.description}
            image={item.image}
            onPress={() =>
              navigation.navigate("ChatScreen", {
                id: item.id,
                person: item.title,
                image: item.image,
              })
            }
            renderRightActions={() => (
              <ListItemDeleteAction onPress={() => handleDelete(item)} />
            )}
          />
        )}
        refreshing={refreshing}
        onRefresh={() => {
          setMessages([
            {
              id: 2,
              title: "Fatima Medlij",
              description: "Hello Bestie",
              image: require("../assets/default.jpeg"),
            },
            {
              id: 3,
              title: "Mohammad ",
              description: "salam aleikom",
              image: require("../assets/default1.jpeg"),
            },
          ]);
        }}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  layout: {
    paddingTop: Constants.statusBarHeight,
    flex: 1,
  },
});

export default MessagesScreen;
