import * as React from "react";
import ListItem from "../../components/ListItem";
import { FlatList, View, StyleSheet, Text } from "react-native";

const reviews = [
  {
    id: 1,
    name: "Elie Kozah",
    review: "Great job wow highly recommend wow",
    image: require("../../assets/default.jpg"),
  },
  {
    id: 2,
    name: "Fatima Medlij",
    review: "Horrible hate it oh no",
    image: require("../../assets/default.jpg"),
  },
  {
    id: 3,
    name: "Rahaf Zaiter",
    review: "Loved it, good prices and great work!",
    image: require("../../assets/default.jpg"),
  },
];
import ProviderDetails from "../ProviderDetails";
function ProviderDetailsScreen({ navigation }, { props }) {
  return (
    <View>
      <ProviderDetails />
      <View style={styles.layout}>
        <Text style={styles.header}>Reviews</Text>
        <FlatList
          data={reviews}
          keyExtractor={(review) => review.id.toString()}
          renderItem={({ item }) => (
            <ListItem
              title={item.name}
              subTitle={item.review}
              image={item.image}
            />
          )}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  header: {
    fontSize: 20,
    fontWeight: "400",
    marginBottom: 10,
  },
  layout: {
    flex: 1,
    marginHorizontal: 10,
  },
});
export default ProviderDetailsScreen;
