import { StyleSheet, Text, View, Button } from "react-native";
import colors from "../../config/colors";

const info = [
  {
    id: 1,
    name: "Fatima",
    image: require("../../../assets/default.jpg"),
    location: "Beirut, Cornich Mazraa"
  },

]
const ClientProfile = ({ navigation },props) => {
  const [info, setInfo] = useState(info);
  return (
    <View style={styles.container}>
      <Image style={styles.image} source={info.image}/>
      <Text style={styles.name}> {info.name} </Text>
      <Text style={styles.location}>{info.location}</Text>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  image:{
    height: 200,
    width:200,
    borderRadius: 100,
    borderWidth: 10,
    borderColor: colors.myblue,
  },
  location:{

  },
  name: {
    fontSize: 50,
    fontWeight: "bold",
  },
});
export default ClientProfile;
