import React, { useState } from "react";
import { StyleSheet, onPress, View, TouchableHighlight } from "react-native";
import { FlatList } from "react-native";
import ListItem from "../ListItem";
import ListItemDeleteAction from "../ListItemDeleteAction";
import Constants from "expo-constants";
import colors from "../config/colors"

const initialMessages = [
  {
    id: 1,
    title: "Elie Kozah",
    description: "Text message tex text tex",
    image: require("../../assets/default.jpg"),
  },
  {
    id: 2,
    title: "Fatima Medlij",
    description: "Hello Bestie",
    image: require("../../assets/default.jpg"),
  },
  {
    id: 3,
    title: "Mohammad",
    description: "salam aleikom",
    image: require("../../assets/default.jpg"),
  },
  {
    id: 4,
    title: "Wissam",
    description: "Stan G idle!!",
    image: require("../../assets/default.jpg"),
  },
  {
    id: 5,
    title: "Zaynab",
    description: "Maraheb ",
    image: require("../../assets/default.jpg"),
  },
];
function MessagesScreen(props) {
  const [messages, setMessages] = useState(initialMessages);
  const [refreshing, setRefreshing] = useState(false);

  const handleDelete = (message) => {
    // Delete the message from messages
    setMessages(messages.filter((m) => m.id !== message.id));
  };

  return (
    <View style={styles.layout}>
      <TouchableHighlight underlayColor={colors.text_holder} onPress={onPress}>
        <FlatList
          data={messages}
          keyExtractor={(message) => message.id.toString()}
          renderItem={({ item }) => (
            <ListItem
              title={item.title}
              subTitle={item.description}
              image={item.image}
              onPress={() => console.log("Message selected", item)}
              renderRightActions={() => (
                <ListItemDeleteAction onPress={() => handleDelete(item)} />
              )}
            />
          )}
          refreshing={refreshing}
          onRefresh={() => {
            setMessages([
              {
                id: 2,
                title: "T2",
                description: "D2",
                image: require("../../assets/default.jpg"),
              },
            ]);
          }}
        />
      </TouchableHighlight>
    </View>
  );
}

const styles = StyleSheet.create({
  layout: {
    paddingTop: Constants.statusBarHeight,
  },
});

export default MessagesScreen;
