import React from "react";
import {
  StyleSheet,
  Text,
  TouchableHighlight,
} from "react-native";
import Swipeable from "react-native-gesture-handler/Swipeable";
import colors from "../config/colors";

function ReviewItem({ title, subTitle}) {
  return (
      <TouchableHighlight underlayColor={colors.babygrey}>
        <Text numberOfLines={1} style={styles.title}>
          {title}
        </Text>
        <Text style={styles.subTitle}>
          {subTitle}
        </Text>
      </TouchableHighlight>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: colors.babygrey,
    flexDirection: "row",
    width: "100%",
    maxHeight: 80,
    flex: 1,
    padding: 10,
    marginBottom: 5,
    paddingTop: 10,
  },
  subTitle: {
    color: colors.black,
    marginTop: 6,
    fontStyle: "italic",
  },
  title: {
    marginTop: 10,
    fontWeight: "500",
  },
});

export default ReviewItem;
