export default function ClientBrowse({ navigation: { navigate } }) {
  const route = useRoute();
  let category = route.params.category;

  let output = [];

  axios.get("http://127.0.0.1:8000/api/list/" + category).then((response) => {
    if (response.status === 200) {
      let listings = response.data;
      output.push(
        <FlatList
          data={listings}
          keyExtractor={(listing) => listing.id.toString()}
          renderItem={({ item }) => (
            <TouchableOpacity
              onPress={() =>
                navigate("ProviderDetailsScreen", {
                  name: item.name,
                  id: item.id,
                  location: item.location,
                  image: item.avatar,
                  category: item.category,
                  rating: item.rating,
                  hourly_rate: item.hourly_rate,
                })
              }
            >
              <ProviderCard
                name={item.name}
                location={item.location}
                category={item.category}
                image={item.image}
                rating={item.rating}
              />
            </TouchableOpacity>
          )}
        />
      );
      // console.log(output);
      // console.log(listings);
    } else {
      output.push(
        <>
          <Text style={styles.error_text}>Failed To Fetch Data</Text>
        </>
      );
    }
  });
  return (
    <View style={styles.layout}>
      <View style={styles.searchbar}>
        <TextInput
          style={styles.inputBox}
          placeholder="Search Providers"
          placeholderTextColor={colors.text_holder}
          color={colors.black}
        />
        <TouchableOpacity style={styles.button}>
          <MaterialCommunityIcons
            name="account-search"
            size={25}
            color="white"
          />
        </TouchableOpacity>
      </View>
      <View>{output}</View>

      {/* <FlatList
