import { FlatList, View, StyleSheet, Text, Image } from "react-native";
import React, { useEffect, useState } from "react";

import reviewsApi from "../../api/reviews";
import ProviderDetails from "../../components/ProviderDetails";
import ReviewItem from "../../components/ReviewItem";
import { useRoute } from "@react-navigation/native";
import useApi from "../../hooks/useApi";

function ProviderDetailsScreen() {
  const [final] = useState([]);
  const [noreviews, setNoReviws] = useState(false);
  const [item, setItem] = useState([]);

  const route = useRoute();
  const [t_id] = useState(route.params.id);

  const {
    data: reviews,
    loading,
    request: loadReviews,
  } = useApi(reviewsApi.getReviews);

  useEffect(() => {
    // loadReviews(t_id);
    getNames();
  }, []);

  const getNames = async () => {
    // await loadReviews;
    // console.log(reviews.length);
    // for (let i = 0; i < reviews.length; i++) {
    //   setC_id(reviews[i].client_id);
    //   console.log(c_id);
    // }
    const res = await loadReviews(t_id);
    if (res.data[1]) {
      setNoReviws(false);
      for (let i = 0; i < 20; i++) {
        console.log(res.data[i].client_id);
        const { data } = await reviewsApi.getName(res.data[i].client_id);
        // clientname.push(data.name, data.id);
        const item = [res.data[i].review, res.data[i].star_rating, data.name];
        // console.log(final);
        console.log(item);
        final.push(item);
      }
    } else {
      setNoReviws(true);
    }
    console.log(final);
  };
  // getNames();

  return (
    <>
      <ProviderDetails />
      <View style={styles.layout}>
        {!noreviews && <Text style={styles.header}>Reviews</Text>}
        {loading && (
          <Image
            source={require("../../assets/loading.gif")}
            style={styles.gif}
          />
        )}
        {noreviews && <Text style={styles.text}>No Reviews yet!</Text>}
        <FlatList
          data={final}
          keyExtractor={(review) => review.id.toString()}
          renderItem={({ item }) => (
            <ReviewItem
              title={item.name}
              subTitle={item.review}
              rating={item.star_rating}
            />
          )}
        />
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  gif: {
    height: 300,
    width: 300,
    alignSelf: "center",
  },
  header: {
    fontSize: 20,
    fontWeight: "400",
    marginBottom: 10,
  },
  layout: {
    flex: 1,
    marginHorizontal: 10,
  },
  text: {
    fontSize: 20,
    alignSelf: "center",
    marginTop: 20,
  },
});
export default ProviderDetailsScreen;
