import React, { useState, useEffect, useContext } from "react";
import { StyleSheet, Text, View, TouchableOpacity } from "react-native";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import { MaterialIcons } from "@expo/vector-icons";
import colors from "../../config/colors";
import detailsApi from "../../api/users";
import ImageInput from "../../components/ImageInput";
import UserContext from "../../id/context";
import useLocation from "../../hooks/useLocation";
import AuthContext from "../../auth/context";

function ClientProfile({ navigation }) {
  const {user_id} = useContext(UserContext)
  const [my_id] = useState(user_id);
  const {auth, setAuth} =useContext(AuthContext)
  const [imageUri, setImageUri] = useState();
  const location = useLocation();
  const [data, setData] = useState([]);

  useEffect(() => {
    myDetails();
  }, []);

  const myDetails = async () => {
    const response = await detailsApi.myDetails(my_id);
    setData(response.data);
    setImageUri(response.data.avatar);
  };

  const handleEdit = async () => {
    // const response = await detailsApi.update();
    // setData(response.data);
    // setImageUri(response.data.avatar);
    console.log("Edit");
  };

  return (
    <View style={styles.container}>
      <ImageInput
        imageUri={imageUri}
        onChangeImage={(uri) => setImageUri(uri)}
      />
      <Text style={styles.name}>{data.name}</Text>
      <Text style={styles.location}>{location}</Text>
      <View style={styles.buttoncontainer}>
        <TouchableOpacity onPress={handleEdit} style={styles.button}>
          <MaterialCommunityIcons
            name="account-edit"
            size={24}
            color={colors.text_holder}
          />
          <Text style={styles.buttontext}>Edit Profile</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.button}
          onPress={() => setUser(null)}
        >
          <MaterialIcons name="logout" size={24} color={colors.text_holder} />
          <Text style={styles.buttontext}>Logout</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  button: {
    alignContent: "center",
    justifyContent: "center",
    flexDirection: "row",
    padding: 10,
  },
  buttoncontainer: {
    marginTop: 30,
    alignContent: "center",
    justifyContent: "center",
  },
  buttontext: {
    marginLeft: 10,
    fontSize: 15,
    color: colors.text_holder,
  },
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 10,
  },
  location: {
    fontSize: 15,
  },
  name: {
    marginTop: 10,
    fontSize: 22,
    fontWeight: "bold",
    marginBottom: 10,
  },
});
export default ClientProfile;
