import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  FlatList,
} from "react-native";
import * as Location from "expo-location";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import { MaterialIcons } from "@expo/vector-icons";
import colors from "../../config/colors";
import ImageInput from "../../components/ImageInput";
import useLocation from "../../hooks/useLocation";
import { Fontisto } from "@expo/vector-icons";

const data = [
  {
    id: 1,
    name: "Elie Kozah",
    review: "Great job wow highly recommend wow",
    image: require("../../assets/default.jpg"),
    rating: 3,
  },
  {
    id: 2,
    name: "Fatima Medlij",
    review:
      "Horrible hate it oh no this is a long long long review i will be very detailed and say anything long text long text yes one two three the lazy fox jumped over the i forgot",
    image: require("../../assets/default.jpg"),
    rating: 3,
  },
  {
    id: 3,
    name: "Rahaf Zaiter",
    review: "Loved it, good prices and great work!",
    image: require("../../assets/default.jpg"),
    rating: 3,
  },
  {
    id: 4,
    name: "Elie Kozah",
    review: "Great job wow highly recommend wow",
    image: require("../../assets/default.jpg"),
    rating: 1,
  },
  {
    id: 5,
    name: "Fatima Medlij",
    review: "Horrible hate it oh no",
    image: require("../../assets/default.jpg"),
    rating: 2,
  },
  {
    id: 6,
    name: "Rahaf Zaiter",
    review:
      "Horrible hate it oh no this is a long long long review i will be very detailed and say anything long text long text yes one two three the lazy fox jumped over the i forgot",
    image: require("../../assets/default.jpg"),
    rating: 4,
  },
  {
    id: 7,
    name: "Elie Kozah",
    review: "Great job wow highly recommend wow",
    image: require("../../assets/default.jpg"),
    rating: 5,
  },
];
const ProviderProfile = ({ navigation }, { props }) => {
  const [imageUri, setImageUri] = useState();
  const location = useLocation();
  const rating = 5;
  var stars = [];
  for (let i = rating; i >= 1; i--) {
    stars.push(
      <View key={i}>
        <Fontisto name="star" size={20} color={colors.myyellow} />
      </View>
    );
  }

  return (
    // <View style={styles.container}>
    //   <ImageInput/>
    //   <Text style={styles.name}> {name} </Text>
    //   <Text style={styles.location}>{location}</Text>
    // </View>
    <View style={styles.container}>
      <ImageInput
        imageUri={imageUri}
        onChangeImage={(uri) => setImageUri(uri)}
      />
      <Text style={styles.name}>Fatima Medlij </Text>
      <View style={styles.ratingcontainer}>{stars}</View>
      <Text style={styles.location}>{location}</Text>
      {/* Dummy Data Above */}
      <View style={styles.buttoncontainer}>
        <TouchableOpacity style={styles.button}>
          <MaterialCommunityIcons
            name="account-edit"
            size={24}
            color={colors.text_holder}
          />
          <Text style={styles.buttontext}>Edit Profile</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate("AuthStack")}
        >
          <MaterialIcons name="logout" size={24} color={colors.text_holder} />
          <Text style={styles.buttontext}>Logout</Text>
        </TouchableOpacity>
      </View>
      <FlatList
        data={data}
        onPress={() => console.log("request", item)}
        keyExtractor={(request) => request.id.toString()}
        renderItem={({ item }) => (
          <RequestItem
            name={item.name}
            location={item.location}
            date_time={item.time}
          />
        )}
      />
    </View>
  );
};
const styles = StyleSheet.create({
  button: {
    alignContent: "center",
    justifyContent: "center",
    flexDirection: "row",
    padding: 10,
  },
  buttoncontainer: {
    marginTop: 30,
    alignContent: "center",
    justifyContent: "center",
  },
  buttontext: {
    marginLeft: 10,
    fontSize: 15,
    color: colors.text_holder,
  },
  container: {
    flex: 1,
    marginTop: 40,
    justifyContent: "flex-start",
    alignItems: "center",
    padding: 30,
  },
  image: {
    height: 200,
    width: 200,
    borderRadius: 100,
    borderWidth: 8,
    borderColor: colors.myblue,
    padding: 10,
  },
  location: {
    fontSize: 15,
  },
  name: {
    marginTop: 10,
    fontSize: 20,
    fontWeight: "bold",
  },
  ratingcontainer: {
    flexDirection: "row",
    alignContent: "space-between",
    justifyContent: "space-between",
    marginVertical: 10,
  },
});
export default ProviderProfile;
